function selectSort(list) {
    for (var i=0;i<list.length-1;i++){
        var selected = i;
        for (var j=i;j<list.length;j++){
            if(list[j]<list[selected]){
                selected =j;
            }            
        }
        if(i !== selected){
            [list[selected],list[i]]=[list[i],list[selected]];
        }       
    }
	return list;
}

