function solve(arr,n){
    const s=new Set();
    for(let i=0;i<=arr.length;i++){
        for(var j=i+1;j<=arr.length;j++){
            const remain=arr.slice(i,j).reduce((x,y)=>x+y,0) % n;
            s.add(remain);
        }				
    }
    return s.has(0);
}
