//\u8F93\u51FA\u7ED3\u679C\uFF1A [1,2,3,6,9,8,7,4,5]

function createArray(rows, cols) {
  return Array.from({ length: rows }, () => new Array(cols).fill(false));
}

function spiralOrder(matrix) {
  let res = [];
  if (matrix.length === 0) return res;
  let n = matrix.length; // \u51E0\u884C
  let m = matrix[0].length; // \u51E0\u5217
  // \u8BBE\u8BA1\u65B9\u5411\u5411\u91CF  \u53F3 -> \u4E0B -> \u5DE6 -> \u4E0A
  let dx = [0, 1, 0, -1];
  let dy = [1, 0, -1, 0];
  // \u5224\u65AD\u6570\u5B57\u6709\u6CA1\u6709\u88AB\u5360\u7528
  let st = createArray(n, m);

  // x\u548Cy\u662F\u5F53\u524D\u5FAA\u73AF\u5230\u4E86\u54EA\u4E2A\u5750\u6807\uFF0Cdir\u8868\u793A\u5F53\u524D\u7684\u65B9\u5411
  for (let i = 0, x = 0, y = 0, dir = 0; i < n * m; i++) {
    res.push(matrix[x][y]); // \u6DFB\u52A0\u5230\u7B54\u6848\u6570\u7EC4\u4E2D
    st[x][y] = true; // \u8BF4\u660E\u8BE5\u6570\u5B57\u5DF2\u7ECF\u4F7F\u7528\u8FC7\u4E86

    let a = x + dx[dir],
      b = y + dy[dir];

    // \u5982\u679C x\uFF0Cy\u8D85\u51FA\u8FB9\u754C \u6216\u8005\u5F53\u524D\u5DF2\u7ECF\u4F7F\u7528\u8FC7 \u4F1A\u6539\u65B9\u5411
    if (a < 0 || a >= n || b < 0 || b >= m || st[a][b]) {
      dir = (dir + 1) % 4;
      a = x + dx[dir];
      b = y + dy[dir];
    }

    x = a;
    y = b;
  }
  return res;
}
