        //\u6700\u5927\u884C\u6570
        maxLines: 2,
        //\u6587\u672C\u5C45\u4E2D
        textAlign: TextAlign.center,
        //\u7C97\u4F53
        fontWeight: FontWeight.w400,
        //\u6587\u5B57\u6837\u5F0F FontStyle.italic \u4E3A\u659C\u4F53
        fontStyle: FontStyle.normal,
        //\u6587\u672C\u5927\u5C0F
        fontSize: 24.0,
        //\u914D\u7F6E\u8D85\u51FA\u8303\u56F4\u65F6 \u6587\u672C\u6BB5\u843D\u7ED3\u5C3E\u65F6\u663E\u793A\u7684\u5185\u5BB9
