import { useEffect } from 'react';

//\u8FD8\u662F\u9700\u8981\u4E00\u4E2Aerror.tsx\uFF0Cglobal-error.tsx\u662F\u4E3A\u4E86layout\u548Ctemplate\u7684
export default function Error({
  error,
  reset,
}: {
  error: Error,
  reset: () => void,
}) {
  useEffect(() => {
    console.error(error);
  }, [error]);

  return (
    <div>
      <h2>Something went wrong!</h2>
      <button
        onClick={
          // \u5C1D\u8BD5\u6062\u590D
          () => reset()
        }
      >
        Try again
      </button>
    </div>
  );
}
