export default class ContextText extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0,
    };
  }
  add = () => {
    this.setState({
      counter: this.state.counter + 1,
    });
  };
  render() {
    return (
      <Provider value={{ counter: this.state.counter, add: this.add }}>
        <Consumer>{(value) => <Child {...value} />}</Consumer>
      </Provider>
    );
  }
}
