export default class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
    };
  }
  componentDidCatch(error, info) {
    this.setState({
      hasError: true,
      error: error,
      errorInfo: info,
    });
  }

  render() {
    if (this.state.hasError) {
      return <div>{this.state.error.toString()} </div>;
    }
    return this.props.children;
  }
}
