`,paraId:47,tocIndex:22},{value:`//=>UTILS\u64CD\u4F5CCSS\u5DE5\u5177\u5E93
let utils = (function () {
  //=>\u83B7\u53D6\u6837\u5F0F
  let getCss = (ele, attr) => {
    let val = null,
      reg = /^-?\\d+(\\.\\d+)?(px|rem|em)?$/;
    if ("getComputedStyle" in window) {
      val = window.getComputedStyle(ele)[attr];
      if (reg.test(val)) {
        val = parseFloat(val);
      }
    }
    return val;
  };

  //=>\u8BBE\u7F6E\u6837\u5F0F
  let setCss = (ele, attr, value) => {
    if (!isNaN(value)) {
      if (!/^(opacity|zIndex)$/.test(attr)) {
        value += "px";
      }
    }
    ele["style"][attr] = value;
  };

  //=>\u6279\u91CF\u8BBE\u7F6E\u6837\u5F0F
  let setGroupCss = (ele, options) => {
    for (let attr in options) {
      if (options.hasOwnProperty(attr)) {
        setCss(ele, attr, options[attr]);
      }
    }
  };

  //=>\u5408\u5E76\u4E3A\u4E00\u4E2A
  let css = (...arg) => {
    let len = arg.length,
      fn = getCss;
    if (len >= 3) {
      fn = setCss;
    }
    if (len === 2 && typeof arg[1] === "object") {
      fn = setGroupCss;
    }
    return fn(...arg);
  };

  //=>EACH\uFF1A\u904D\u5386\u5BF9\u8C61\u3001\u6570\u7EC4\u3001\u7C7B\u6570\u7EC4
  let each = (obj, callback) => {
    if ("length" in obj) {
      for (let i = 0; i < obj.length; i++) {
        let item = obj[i],
          res = callback && callback.call(item, i, item);
        if (res === false) {
          break;
        }
      }
      return;
    }
    for (let attr in obj) {
      if (obj.hasOwnProperty(attr)) {
        let item = obj[attr],
          res = callback && callback.call(item, attr, item);
        if (res === false) {
          break;
        }
      }
    }
  };

  return { css, each };
})();

//=>ANIMATE\u52A8\u753B\u5E93
~(function () {
  //=>\u5300\u901F\u8FD0\u52A8\u516C\u5F0F
  let effect = {
    Linear: (t, b, c, d) => (t / d) * c + b,
  };

  //=>\u5F00\u59CB\u8FD0\u52A8
  window.animate = function animate(ele, target, duration = 1000, callback) {
    //=>\u53C2\u6570\u5904\u7406(\u4F20\u9012\u4E09\u4E2A\u503C,\u7B2C\u4E09\u4E2A\u503C\u662F\u51FD\u6570,\u5176\u5B9E\u672C\u8EAB\u60F3\u8981\u4EE3\u8868\u7684\u610F\u601D\uFF1A\u7B2C\u4E09\u4E2A\u662F\u56DE\u8C03\u51FD\u6570\uFF0C\u603B\u65F6\u95F4\u662F\u9ED8\u8BA4\u503C\u5373\u53EF)
    if (typeof duration === "function") {
      callback = duration;
      duration = 1000;
    }

    //=>\u51C6\u5907\u6570\u636E
    let time = 0,
      begin = {},
      change = {};
    utils.each(target, (key, value) => {
      begin[key] = utils.css(ele, key);
      change[key] = value - begin[key];
    });

    //=>\u8BBE\u7F6E\u65B0\u52A8\u753B\u4E4B\u524D\u6E05\u9664\u539F\u6709\u6B63\u5728\u8FD0\u884C\u7684\u52A8\u753B
    clearInterval(ele.animateTimer);
    ele.animateTimer = setInterval(() => {
      time += 17;
      //->\u52A8\u753B\u7ED3\u675F
      if (time >= duration) {
        clearInterval(ele.animateTimer);
        utils.css(ele, target);
        callback && callback.call(ele);
        return;
      }
      //->\u83B7\u53D6\u6BCF\u4E2A\u65B9\u5411\u7684\u5F53\u524D\u4F4D\u7F6E\uFF0C\u5E76\u4E14\u7ED9\u5143\u7D20\u8BBE\u7F6E
      utils.each(target, (key, value) => {
        let cur = effect.Linear(time, begin[key], change[key], duration);
        utils.css(ele, key, cur);
      });
    }, 17);
  };
})();
