let removeDuplicates=function(arr){
    if(!arr) return arr;
    let slow=0,fast=0,right=arr.length;
    while(fast < right){
        if(arr[slow] != arr[fast]){
            slow++;
            arr[slow]=arr[fast]
        }
        fast++;
    }
    return arr
}
