// \u6392\u5E8F\u540E\uFF0C\u6570\u7EC4\u53D8\u4E3A [0,1,9,16,100]
let sortedSquares=function(nums){
    let i=0,j=nums.length-1;
    let result=new Array(nums.length);
    let k=j
    while(i <= j){
        let left=nums[i]*nums[i]
        let right=nums[j]*nums[j]
        if(left < right){
            result[k]=right;
            j--
        }else{
            result[k]=left;
            i++;
        }
        k--
    }
    return result
}

