    if (json[stack[stack.length - 1]] === strArr[i]) {
      // \u5982\u679Cstack\u6570\u7EC4\u4E2D\u7684\u6700\u540E\u4E00\u9879\u7B49\u4E8E\u5B57\u7B26\u4E32\u4E2D\u7684\u67D0\u4E00\u9879\uFF0C\u5220\u6389stack\u4E2D\u7684\u6700\u540E\u4E00\u9879
      stack.pop();
    } else {
      stack.push(strArr[i]); //\u5982\u679Cstack\u6570\u7EC4\u4E2D\u7684\u6700\u540E\u4E00\u9879\u4E0D\u7B49\u4E8E\u5B57\u7B26\u4E32\u4E2D\u7684\u67D0\u4E00\u4E2A\u5143\u7D20
    }
