function createRoot(container) {
  // \u521B\u5EFAdom\u5BB9\u5668
  return {
    // \u628A\u865A\u62DF dom\u53D8\u6210\u771F\u5B9E dom\uFF0C\u5E76\u4E14\u63D2\u5165\u5BB9\u5668 container
    render(rootVdom) {
      mountVdom(rootVdom, container);
    },
  };
}

function mountVdom(vdom, parentDOM) {
  // \u628A\u865A\u62DF dom\u8F6C\u6210\u771F\u5B9Edom
  const domElement = createDOMElement(vdom);
  // \u628A\u6B64\u771F\u5B9E dom,\u6DFB\u52A0\u5230\u5BB9\u5668\u4E2D
  parentDOM.appendChild(domElement);
}

function createDOMElementFromTextComponent(vdom) {
  const { props } = vdom;
  const domElement = document.createTextNode(props);
  vdom.domElement = domElement;
  return domElement;
}

function createDOMElementFromClassComponent(vdom) {
  const { props, type } = vdom;
  const classInstance = new type(props);
  const renderVdom = classInstance.render();
  const domeElement = createDOMElement(renderVdom);
  return domeElement;
}

function createDOMElementFromFunctionComponent(vdom) {
  const { type, props } = vdom;
  const functionElement = type(props);
  const domeElement = createDOMElement(functionElement);
  // \u628A\u51FD\u6570\u7EC4\u4EF6\u8FD4\u56DE\u7684 React\u5143\u7D20\u4F20\u9012\u7ED9 renderElement,\u521B\u5EFA\u771F\u5B9E dom
  return domeElement;
}

function updateProps(domElement, oldProps = {}, newProps = {}) {
  Object.keys(newProps).forEach((name) => {
    // \u540E\u9762\u5904\u7406 children
    if (name === 'children') return;
    // \u5982\u679C\u662F\u884C\u5185\u592E\u89C6\u5C5E\u6027\uFF0C\u5219\u76F4\u63A5\u8986\u76D6\u6389\u771F\u5B9E dom\u7684 style\u4E0A
    if (name == 'style') {
      // Object.assign(target,obj),\u8FD4\u56DE target
      Object.assign(domElement.style, newProps.style);
      // \u7B49\u4E8E\u4EE5\u4E0B\u4EE3\u7801
      // for(let key in props.style) {
      //     domElement.style[key] = props.style[key];
      // }
    } else {
      // \u6682\u65F6\u4E0D\u5904\u7406\u4E8B\u4EF6
      domElement[name] = newProps[name];
    }
  });
}
function mountChildren(vdom, domElement) {
  const { props } = vdom;
  // \u683C\u5F0F\u5316 children\u4E3A\u6570\u7EC4
  const children = wrapToArray(props?.children ?? []);
  children.forEach((childVdom) => {
    mountVdom(childVdom, domElement);
  });
}

function createDOMElementFromNativeComponent(vdom) {
  const { type, props } = vdom;

  const domElement = document.createElement(type);
  updateProps(domElement, {}, props);
  // \u628A\u6240\u6709\u5B50\u8282\u70B9\u6302\u5728\u5230\u771F\u662F dom\u4E0A
  mountChildren(vdom, domElement); // \u865A\u62DF dom\uFF0C\u6302\u5728\u5230\u771F\u5B9Edom
  return domElement;
}

// \u865A\u62DF dom\u8F6C\u6210\u771F\u5B9E dom
function createDOMElement(vdom) {
  // \u53D6\u51FA\u5143\u7D20\u7C7B\u578B\u548C\u5C5E\u6027\u5BF9\u8C61
  // {"tools":"div","props":{"style":{"color":"red"},"className":"container","children":["hello",{"tools":"span","props":{"style":{"color":"blue"},"children":"world"}}]}}
  if (isUndefined(vdom)) return null;
  const { type, props } = vdom;
  if (type === REACT_TEXT) {
    return createDOMElementFromTextComponent(vdom);
  } else if (typeof type === 'function') {
    if (type.isReactComponent) {
      // \u5982\u679C\u662F\u7C7B\u7EC4\u4EF6
      return createDOMElementFromClassComponent(vdom);
    } else {
      // \u5982\u679C\u662F\u51FD\u6570\u7EC4\u4EF6
      return createDOMElementFromFunctionComponent(vdom);
    }
  } else {
    return createDOMElementFromNativeComponent(vdom);
  }
}

const ReactDOM = {
  createRoot,
};
export default ReactDOM;
