import {createDOMElement, getDOMElementByVdom} from './react-dom/client'

let isBatchingUpdates = false;
let dirtyComponents = new Set();
export function setIsBatchingUpdates(value) {
  isBatchingUpdates = value;
}
export function flushDirtyComponents() {
  dirtyComponents.forEach((component) => component.forceUpdate());
  dirtyComponents.clear();
  isBatchingUpdates = false;
}

// typeof Component\u8FD4\u56DE\u7684\u4E5F\u662Ffunction\uFF0C class\u53EA\u662F\u4E00\u4E2A\u8BED\u6CD5\u7CD6
class Component{
    // \u7ED9 component\u52A0\u9759\u6001\u5C5E\u6027\uFF0C\u8868\u793A\u662F\u7C7B\u7EC4\u4EF6\u8FD8\u662F\u51FD\u6570\u7EC4\u4EF6
    static isReactComponent=true;
    constructor(props){
        // \u628A\u6536\u96C6\u5230\u7684\u5C5E\u6027\u5BF9\u8C61\uFF0C\u653E\u5230 props\u4E0A
        this.props=props;
        this.pendingStates = [];  // \u8FD9\u91CC\u4FDD\u5B58\u9700\u8981\u8FD8\u6CA1\u6709\u751F\u6548\u7684\u66F4\u65B0 state
    }
    setState(partialState,callback){
        if(isBatchingUpdates){ // \u5982\u679C\u662F\u6279\u5904\u7406
            dirtyComponents.add(this);
            this.pendingStates.push(partialState);
        }else{
            let newState=typeof  partialState == 'function' ? partialState(this.state) : partialState
            this.state=  {
                ...this.state,
                ...newState
            }
            // \u8BA1\u7B97\u5B8C\u65B0\u7684\u72B6\u6001\u8981\u66F4\u65B0\u7EC4\u4EF6
            this.forceUpdate()
            // \u5728\u7EC4\u4EF6\u66F4\u65B0\u4E4B\u540E\u56DE\u8C03
            callback?.()
        }
    }
    // \u6279\u5904\u7406\u5408\u5E76 state
    accumulateState() {
       let state = this.pendingStates.reduce((state, update) => {
             const newState = typeof update === "function" ? update(state) : update;
             return { ...state, ...newState };
           }, this.state);
       this.pendingStates.length = 0;
       return state;
    }

    // vdom classComponent
    // classInstance.oldRenderVdom=render\u5C31\u53BB\u8FD4\u56DE\u7684\u865A\u62DFDOM button\u865A\u62DFDOM
    // vdom.domElement = domElement;
    // button\u865A\u62DFDOM.domElement=\u771F\u5B9EbuttonDOM\u5143\u7D20

    forceUpdate(){
        this.state = this.accumulateState();
        // \u91CD\u65B0\u8C03\u7528 render\u65B9\u6CD5\uFF0C\u8BA1\u7B97\u65B0\u7684\u865A\u62DF Dom
        const renderVdom=this.render();
        // \u66FF\u6362\u8001\u7684\u771F\u5B9E dom  \u8001\u7684\u771F\u5B9E dom => \u8001\u7684\u771F\u5B9E dom\u7236\u8282\u70B9
        const oldDomElement=getDOMElementByVdom(this.oldRenderVdom);
        // \u521B\u5EFA\u65B0\u7684\u771F\u5B9E Dom
        let newDomElement = createDOMElement(renderVdom)
        // \u83B7\u53D6\u7236\u8282\u70B9
        const parentDom=oldDomElement.parentNode;
        // \u628A\u8001 dom\u66FF\u6362\u6389
        parentDom.replaceChild(newDomElement,oldDomElement)
        this.oldRenderVdom=renderVdom
    }
}


const React={
    createElement,
    Component
}

export default React;

`,paraId:42,tocIndex:19},{value:"event.js",paraId:43,tocIndex:19},{value:`//\u5B9A\u4E49\u4E00\u4E2A\u4E8B\u4EF6\u7C7B\u578B\u7684\u65B9\u6CD5\u5B57\u5178
import {flushDirtyComponents, setIsBatchingUpdates} from "../react";

const eventTypeMethods={
    click:{
        capture:'onClickCapture',
        bubble:'onClick'
    }
}
// \u4E8B\u4EF6\u4F20\u64AD\u7684 2 \u4E2A\u9636\u6BB5
const phases=['capture','bubble']
// \u8BBE\u7F6E\u65F6\u95F4\u59D4\u6258,\u628A\u6240\u6709\u65F6\u95F4\u7ED1\u5B9A\u5230 container\u4E0A
function setupEventDelegation(container){
    // \u904D\u5386\u6240\u6709\u7684\u4E8B\u4EF6
    Reflect.ownKeys(eventTypeMethods).forEach((eventType)=>{
        phases.forEach((phase)=>{
            // \u7ED9\u5BB9\u5668\u6DFB\u52A0\u76D1\u542C\u51FD\u6570
            container.addEventListener(eventType,(nativeEvent)=>{
                // \u751F\u6210\u5408\u6210\u4E8B\u4EF6\u5BF9\u8C61
                const syntheticEvent = createSyntheticEvent(nativeEvent);
                // \u6A21\u62DF\u4E8B\u4EF6\u4F20\u64AD\u7684\u987A\u5E8F\uFF0C\u4E8B\u4EF6\u4F20\u9012\u8DEF\u5F84\u4E0A\u6240\u6709\u7684 Dom\u5143\u7D20\u4E0A\u7ED1\u5B9A\u7684 React\u4E8B\u4EF6\u53D6\u51FA\u6765\u6309\u987A\u5E8F\u6267\u884C
                // composedPath\u8FD4\u56DE eventTarget\u5BF9\u8C61\u6570\u7EC4\u3002 [button,div,root,body,html,document,window]
                const composedPath=syntheticEvent.composedPath();
                // \u5982\u679C\u662F\u6355\u83B7\u9636\u6BB5\u9700\u8981\u5012\u5E8F
                const domElements=phase === 'capture' ? composedPath.reverse():composedPath;
                setIsBatchingUpdates(true); // \u8BBE\u7F6E\u4E3A\u6279\u5904\u7406
                // \u904D\u5386\u6240\u6709\u7684 Dom\u5143\u7D20 \u6267\u884C\u5B83\u8EAB\u4E0A\u7ED1\u5B9A\u7684 React\u76D1\u542C\u51FD\u6570
                // domElement.reactEvents[onClick] = \u5BF9\u5E94\u7684\u76D1\u542C\u51FD\u6570
                const methodName=eventTypeMethods[eventType][phase];
                for(let domElement of domElements){
                    // \u5982\u679C\u963B\u6B62\u4E8B\u4EF6\u5192\u6CE1\u4E86\u4E0D\u6267\u884C
                    if(syntheticEvent.isPropagationStopped()){
                        break;
                    }
                    //currentTarget\u662F\u4F1A\u53D8\u7684\uFF0C\u800Ctarget\u662F\u4E0D\u53D8\u7684
                    syntheticEvent.currentTarget = domElement;
                    // \u5982\u679C\u6B64 Dom\u8282\u70B9\u4E0A\u6709\u56DE\u8C03\u51FD\u6570\uFF0C\u5219\u6267\u884C
                    domElement.reactEvents?.[methodName]?.(syntheticEvent);
                }
                flushDirtyComponents(); // \u6267\u884C\u6279\u5904\u7406\u66F4\u65B0 dom\u903B\u8F91
            },phase === 'capture');
        })
    })
}
