function useEffect(callback, depArray) {
  const hasDeps = depArray?.length; //\u662F\u5426\u5B58\u5728\u4F9D\u8D56\u9879
  //\u5224\u65AD,\u4E24\u6B21\u7684deps\u662F\u5426\u5B8C\u5168\u76F8\u540C
  const hasChangedDeps = _deps
    ? depArray.every((el, i) => el === deps[i])
    : true;
  // \u6CA1\u6709\u4F9D\u8D56\uFF0C\u6216deps\u53D1\u751F\u53D8\u5316\u7684\u65F6\u5019
  if (!hasDeps || hasChangedDeps) {
    callback();
    _deps = depArray;
  }
}
