class Node {
  constructor(key) {
    this.key = key;
    this.left = null;
    this.right = null;
  }
}
class BinarySearchTree {
  constructor() {
    this.root = null;
  }
  insert(key) {
    let newNode = new Node(key);
    if (!this.root) {
      this.root = newNode;
    } else {
      this.insertNode(this.root, newNode);
    }
  }
  insertNode(root, newNode) {
    // \u5982\u679C\u65B0\u589E\u7684\u503C\u5C0F\u4E8E\u6839\u8282\u70B9\u7684\u503C \u8D70\u5DE6\u8FB9
    if (newNode.key < root.key) {
      // \u5206\u4E24\u79CD\u60C5\u51B5\uFF1A1.\u6CA1\u5DE6\u5B50\u8282\u70B9  2.\u6709\u5B50\u8282\u70B9\uFF0C\u8DDF\u5DE6\u8282\u70B9\u6BD4\u8F83
      if (root.left == null) {
        root.left = newNode;
      } else {
        this.insertNode(root.left, newNode);
      }
    } else {
      if (root.right == null) {
        root.right = newNode;
      } else {
        this.insertNode(root.right, newNode);
      }
    }
  }
  // \u5148\u5E8F\u904D\u5386  1.\u8BBF\u95EE\u6839\u8282\u70B9  2.\u904D\u5386\u5DE6\u5B50\u6811  3.\u904D\u5386\u53F3\u5B50\u6811
  preOrderTraverse(handler) {
    preOrderTraverseNode(this.root, handler);
  }
  // \u4E2D\u5E8F\u904D\u5386  1.\u904D\u5386\u5DE6\u5B50\u6811  2.\u8BBF\u95EE\u6839\u8282\u70B9  3.\u904D\u5386\u53F3\u5B50\u6811
  inOrderTraverse(handler) {
    inOrderTraverseNode(this.root, handler);
  }
  // \u540E\u5E8F\u904D\u5386  1.\u904D\u5386\u5DE6\u5B50\u6811  2.\u904D\u5386\u53F3\u5B50\u6811  3.\u8BBF\u95EE\u6839\u8282\u70B9
  postOrderTraverse(handler) {
    postOrderTraverseNode(this.root, handler);
  }
  // \u6700\u5C0F\u503C
  min() {
    let node = this.root;
    if (node) {
      while (node.left !== null) {
        node = node.left;
      }
      return node.key;
    }
    return null;
  }
  // \u6700\u5927\u503C
  max() {
    let node = this.root;
    if (node) {
      while (node.right !== null) {
        node = node.right;
      }
      return node.key;
    }
    return null;
  }
  // \u641C\u7D22\u67D0\u4E2A\u8282\u70B9
  search(key) {
    return this.searchNode(this.root, key);
  }
  // search(key){
  //     let node = this.root;
  //     while(node){
  //         if(key < node.key){
  //             node=node.left;
  //         }else if(key > node.key){
  //             node=node.right;
  //         }else{
  //             return true;
  //         }
  //     }
  //     return false;
  // }
  searchNode(node, key) {
    // 1.\u5982\u679C\u4F20\u5165\u7684node\u4E3Anull\u90A3\u4E48, \u90A3\u4E48\u5C31\u9000\u51FA\u9012\u5F52
    if (node === null) {
      return false;
    }
    // 2.\u5224\u65ADnode\u8282\u70B9\u7684\u503C\u548C\u4F20\u5165\u7684key\u5927\u5C0F
    if (key < node.key) {
      // 2.1.\u4F20\u5165\u7684key\u8F83\u5C0F, \u5411\u5DE6\u8FB9\u7EE7\u7EED\u67E5\u627E
      return this.searchNode(node.left, key);
    } else if (key > node.key) {
      // 2.2.\u4F20\u5165\u7684key\u8F83\u5927, \u5411\u53F3\u8FB9\u7EE7\u7EED\u67E5\u627E
      return this.searchNode(node.right, key);
    } else {
      // 2.3.\u76F8\u540C, \u8BF4\u660E\u627E\u5230\u4E86key
      return true;
    }
  }
  // \u5220\u9664\u7684\u4E09\u79CD\u60C5\u51B5\uFF1A1.\u5220\u9664\u7684\u662F\u53F6\u5B50\u8282\u70B9  2.\u5220\u9664\u7684\u53EA\u6709\u4E00\u4E2A\u5B50\u8282\u70B9  3.\u5220\u9664\u7684\u6709\u4E24\u4E2A\u8282\u70B9
  remove(key) {
    // 1.\u5B9A\u4E49\u4E34\u65F6\u4FDD\u5B58\u7684\u53D8\u91CF
    var current = this.root;
    var parent = this.root;
    var isLeftChild = true;

    // 2.\u5F00\u59CB\u67E5\u627E\u8282\u70B9
    while (current.key !== key) {
      parent = current;
      if (key < current.key) {
        isLeftChild = true;
        current = current.left;
      } else {
        isLeftChild = false;
        current = current.right;
      }

      // \u5982\u679C\u53D1\u73B0current\u5DF2\u7ECF\u6307\u5411null, \u90A3\u4E48\u8BF4\u660E\u6CA1\u6709\u627E\u5230\u8981\u5220\u9664\u7684\u6570\u636E
      if (current === null) return false;
    }

    // 3.\u5220\u9664\u7684\u7ED3\u70B9\u662F\u53F6\u7ED3\u70B9
    if (current.left === null && current.right === null) {
      if (current == this.root) {
        this.root == null;
      } else if (isLeftChild) {
        parent.left = null;
      } else {
        parent.right = null;
      }
    }

    // 4.\u5220\u9664\u6709\u4E00\u4E2A\u5B50\u8282\u70B9\u7684\u8282\u70B9
    else if (current.right === null) {
      if (current == this.root) {
        this.root = current.left;
      } else if (isLeftChild) {
        parent.left = current.left;
      } else {
        parent.right = current.left;
      }
    } else if (current.left === null) {
      if (current == this.root) {
        this.root = current.right;
      } else if (isLeftChild) {
        parent.left = current.right;
      } else {
        parent.right = current.right;
      }
    }

    // 5.\u5220\u9664\u6709\u4E24\u4E2A\u8282\u70B9\u7684\u8282\u70B9
    else {
      // 1.\u83B7\u53D6\u540E\u7EE7\u8282\u70B9
      var successor = getSuccessor(current);

      // 2.\u5224\u65AD\u662F\u5426\u662F\u6839\u8282\u70B9
      if (current == this.root) {
        this.root = successor;
      } else if (isLeftChild) {
        parent.left = successor;
      } else {
        parent.right = successor;
      }

      // 3.\u5C06\u5220\u9664\u8282\u70B9\u7684\u5DE6\u5B50\u6811\u8D4B\u503C\u7ED9successor
      successor.left = current.left;
    }

    return true;
  }
}

function getSuccessor(delNode) {
  // 1.\u4F7F\u7528\u53D8\u91CF\u4FDD\u5B58\u4E34\u65F6\u7684\u8282\u70B9
  var successorParent = delNode;
  var successor = delNode;
  var current = delNode.right; // \u8981\u4ECE\u53F3\u5B50\u6811\u5F00\u59CB\u627E

  // 2.\u5BFB\u627E\u8282\u70B9
  while (current != null) {
    successorParent = successor;
    successor = current;
    current = current.left;
  }

  // 3.\u5982\u679C\u662F\u5220\u9664\u56FE\u4E2D15\u7684\u60C5\u51B5, \u8FD8\u9700\u8981\u5982\u4E0B\u4EE3\u7801
  if (successor != delNode.right) {
    successorParent.left = successor.right;
    successor.right = delNode.right;
  }
  return successor;
}
function inOrderTraverseNode(node, cb) {
  if (node !== null) {
    preOrderTraverseNode(node.left, cb);
    cb(node.key);
    preOrderTraverseNode(node.right, cb);
  }
}
function preOrderTraverseNode(node, cb) {
  if (node !== null) {
    cb(node.key);
    preOrderTraverseNode(node.left, cb);
    preOrderTraverseNode(node.right, cb);
  }
}

function postOrderTraverseNode(node, cb) {
  if (node !== null) {
    preOrderTraverseNode(node.left, cb);
    preOrderTraverseNode(node.right, cb);
    cb(node.key);
  }
}

