function withTiming(wrappedComp) {
  let start, end;
  return class extends WrappedComp {
    constructor(props) {
      super(props);
      start = 0;
      end = 0;
    }
    componentWillMount() {
      if (super.componentWillMount) {
        super.componentWillMount();
      }
      start = Date.now();
    };
    componentDidMount() {
      if (super.componentDidMount) {
        super.componentDidMount();
      }
      end = Date.now();
      console.log('\u7EC4\u4EF6\u6E32\u67D3\u8017\u65F6:', end - start, 'ms')
    };
  };
};

